using SFML.Graphics;
using SFML.Window;
using SFML.System;

namespace RaycastGame
{
    public static class Settings
    {
        // PLAYER SETTINGS
        private static float mouseSensativity = 0.3f;
        public static float MouseSensativity { get { return mouseSensativity; } set { mouseSensativity = value; } }


        // RAYCAST SETTINGS
        private static int raysCount = (int)Config.GameResolution.X / 1;
        private static int raysCountHalf = raysCount / 2;
        public static int RaysCount { get { return raysCount; } set { raysCount = value; raysCountHalf = raysCount / 2; } }
        public static int RaysCountHalf { get { return raysCountHalf; } }


        // INPUT SETTINGS
        private static Keyboard.Key forwardKey = Keyboard.Key.W;
        private static Keyboard.Key backwardKey = Keyboard.Key.S;
        private static Keyboard.Key leftKey = Keyboard.Key.A;
        private static Keyboard.Key rightKey = Keyboard.Key.D;
        public static Keyboard.Key ForwardKey { get { return forwardKey; } set { forwardKey = value; } }
        public static Keyboard.Key BackwardKey { get { return backwardKey; } set { backwardKey = value; } }
        public static Keyboard.Key LeftKey { get { return leftKey; } set { leftKey = value; } }
        public static Keyboard.Key RightKey { get { return rightKey; } set { rightKey = value; } }


        private static Keyboard.Key menuKey = Keyboard.Key.Escape;
        public static Keyboard.Key MenuKey { get { return menuKey; } set { menuKey = value; } }


        // AUDIO SETTINGS
        private static float volume = 30f;
        public static float Volume { get { return volume; } set { volume = value; } }
    }
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       